    public static ArrayList<Apodid> getListData(){
        ArrayList<Apodid> list = new ArrayList<>();
        for (int position = 0; position < title.length; position++){
            Apodid apodid = new Apodid();
            apodid.setTitle(title[position]);
            apodid.setDate(date[position]);
            apodid.setDetail(detail[position]);
            apodid.setPhoto(photo[position]);
            apodid.setKredit_foto(kredit_foto[position]);
            apodid.setKredit_konten(kredit_konten[position]);
            apodid.setAlih_bahasa(alih_bahasa[position]);
            list.add(apodid);
        }
        return list;
    }

